import React from 'react'
import {useState,useEffect} from 'react'
const initialForm = { username:"",email:""};

function Form({addContact,contacts}) {
    
    const [form,setForm] = useState(initialForm);
    useEffect(()=>{
        setForm(initialForm);
    },[contacts])
    const onChangeInput= (event)=>{
        setForm({...form,[event.target.name]:event.target.value})
    }
    const onSubmit= (event)=>{
        event.preventDefault();
        if(form.username ==='' || form.email ==='') {
            return false;
        }
        addContact([...contacts,form]);
    }
  return (
    <div>
        <form onSubmit={onSubmit}>
        <div><input name='username' placeholder='User Name' value={form.username} onChange={onChangeInput}></input></div>
        <div><input name='email' placeholder='Email' value={form.email} onChange={onChangeInput}></input></div>
        <div><button onClick={onSubmit} >Add</button></div></form>
    </div>
  )
}

export default Form;
