import React from 'react'
import {useState,useEffect} from 'react'
const initialForm = { fullName:"",phoneNumber:""};

function Form(addContact,contacts) {
    console.log(addContact,contacts);
    
    const [form,setForm] = useState(initialForm);
    useEffect(()=>{
        setForm(initialForm);
    },[contacts])
    const onChangeInput= (event)=>{
        setForm({...form,[event.target.name]:event.target.value})
    }
    const onSubmit= (event)=>{
        event.preventDefault();
        if(form. fullName ==='' || form.phoneNumber ==='') {
            return false;
        }
        addContact([form]);
    }
  return (
    <div>
        <form onSubmit={onSubmit}>
        <div><input name='fullName' placeholder='Full Name' value={form.fullName} onChange={onChangeInput}></input></div>
        <div><input name='phoneNumber' placeholder='Phone Number' value={form.phoneNumber} onChange={onChangeInput}></input></div>
        <div><button onClick={onSubmit} >Add</button></div></form>
    </div>
  )
}

export default Form;
