import React from 'react'
import {useState} from 'react'


function List({contacts}) {
    const [users,setUsers] = useState([]); 
    const getUsers=()=>{
        return new Promise (async(resolve,reject)=>{
            const {data} = await axios("https://jsonplaceholder.typicode.com/users");
            console.log(data);
            resolve(data);
        }) 

    }
    const[filterText,setFilterText] = useState('');
    const filtered = contacts.filter((item)=>{
        return Object.keys(item).some((key)=>
            item[key]
            .toString()
            .toLowerCase()
            .includes(filterText.toLocaleLowerCase())
        )
    })
  return (
    <div>
        <input placeholder='filter contact' value={filterText} onChange={(e)=>setFilterText(e.target.value)}></input>
        <ul className='list'>
            {
            filtered.map((contact,i)=><li key={i}><span>{contact.fullName}</span><span>{contact.phoneNumber}</span></li>)
            }
        </ul>
        <p style={{color:'gray'}}> Total contact ({filtered.length})</p>
    </div>
  )
}

export default List;
