import React from 'react'
import {useState,useEffect} from 'react';
import { useFormik } from 'formik';
import * as yup from 'yup'; 
import validationSchema from './validations';
import 'style.css/Contacts';

function Signup() {
    const formik = useFormik({
        initialValues: {
          email: '',
          password: '',
          passwordConfirm: '',
        },
        onSubmit: (values) => {
          console.log(values);
        },
        validationSchema
      });
    
      return (
        <div id="signupcontainer">
          <form onSubmit={formik.handleSubmit}>
            <div>
              <input
                name="email"
                placeholder="Email"
                onChange={formik.handleChange}
                value={formik.values.email} // value prop'unu ekleyin
              />
            </div>
            <div>
              <input
                name="password"
                placeholder="Password"
                onChange={formik.handleChange}
                value={formik.values.password} // value prop'unu ekleyin
              />
            </div>
            <div>
              <input
                name="passwordConfirm"
                placeholder="Password Confirm"
                onChange={formik.handleChange}
                value={formik.values.passwordConfirm} // value prop'unu ekleyin
              />
            </div>
            <div>
            <button type="submit">Add</button>
          </div>
            
          </form>
          <code>{JSON.stringify(formik.values)}</code>
        </div>
      );
    }

export default Signup
