import React from 'react'
import {useState,useEffect} from 'react';
import { useFormik } from 'formik';

function Form({ addContact, contacts }) {
    const {formik, values} = useFormik({
      initialValues: {
        username: '',
        email: '',
        gender: 'female',
        hobies: [],
        country:"Turkey"
      },
      onSubmit: (values) => {
        // addContact([...contacts, values]);
        // formik.resetForm(); // resetForm'u doğrudan formik üzerinden çağırın
        console.log(values);
      },
    });
  
    return (
      <div>
        <form onSubmit={formik.handleSubmit}>
          <div>
            <input
              name="username"
              placeholder="User Name"
              onChange={formik.handleChange}
              value={formik.values.username} // value prop'unu ekleyin
            />
          </div>
          <div>
            <input
              name="email"
              placeholder="Email"
              onChange={formik.handleChange}
              value={formik.values.email} // value prop'unu ekleyin
            />
          </div>
          <div>
          <span>Male</span>
            <input type="radio" name="gender" value="male" onChange={formik.handleChange} checked={values.gender==="male"}/>
          <div>
          <span>Female</span>
            <input type="radio" name="gender" value="female" onChange={formik.handleChange} checked={values.gender==="female"} />
          </div>
          </div>
          <div>
            <button type="submit">Add</button>
          </div>
          <div>
 Football
 <input type="checkbox" name='hobies' value="football" onChange={formik.handleChange} />
</div>

<div>
 Cinema
 <input type="checkbox" name='hobies' value="cinema" onChange={formik.handleChange} />
</div>

<div>
 Photography
 <input type="checkbox" name='hobies' value="photography" onChange={formik.handleChange} />
</div>
<div>
<select name="country" onChange={formik.handleChange} >
 <option value="tr">Turkiye</option>
  <option value="en">England</option>
 <option value="usa">USA</option>
</select>
</div>
        </form>
        <code>{JSON.stringify(values)}</code>
      </div>
    );
  }
  
  export default Form;






