import React from 'react'
import {useState} from 'react'
import User from '../../User';
import {Link,Routes,Route,useRouteMatch} from 'react-router-dom';


function List({contacts,isLoading}) {
    // const {id}=useParams();
    const { path, url } = useRouteMatch();
    const[filterText,setFilterText] = useState('');
    const filtered = contacts.filter((item)=>{
        return Object.keys(item).some((key)=>
            item[key]
            .toString()
            .toLowerCase()
            .includes(filterText.toLocaleLowerCase())
        )
    })
    
  return (
    <div>
        <input placeholder='filter contact' value={filterText} onChange={(e)=>setFilterText(e.target.value)}></input>
        {isLoading &&<div className="lds-roller"><div></div><div></div><div></div><div></div><div></div><div></div><div></div><div></div></div> }
        <ul className='list'>
            {
            filtered.map((contact,i)=><li key={i}><Link to={`${url}/${contact.id}`}><span>{contact.username}</span><span>{contact.email}</span></Link></li>)
            }
        </ul>
        <p style={{color:'gray'}}> Total contact ({filtered.length})</p>
        <Routes>
        <Route path={`${path}/:id`} component={User} />
      </Routes>
    </div>
  )
}

export default List;
