import React from 'react'
import {useState} from 'react'

function Form() {
    const [form,setForm] = useState({ fullName:"",phoneNumber:""})
    const onChangeInput= (event)=>{
        setForm({...form,[event.target.name]:event.target.value})
    }
    const onSubmit= (event)=>{
        setForm({...form,[event.target.name]:event.target.value})
    }
  return (
    <div>
        <form onSubmit={onSubmit}>
        <div><input name='fullName' placeholder='Full Name' value={form.fullName} onChange={onChangeInput}></input></div>
        <div><input name='phoneNumber' placeholder='Phone Number' value={form.phoneNumber} onChange={onChangeInput}></input></div>
        <div><button onClick={onSubmit} >Add</button></div></form>
    </div>
  )
}

export default Form;
