import React from 'react'
import {useState,useEffect} from 'react';
import { Formik } from 'formik';
const initialForm = { username:"",email:""};

function Form({addContact,contacts}) {
    
    const [form,setForm] = useState(initialForm);
    useEffect(()=>{
        setForm(initialForm);
    },[contacts])
    const onChangeInput= (event)=>{
        setForm({...form,[event.target.name]:event.target.value})
    }
    const onSubmit= (event)=>{
        event.preventDefault();
        if(form.username ==='' || form.email ==='') {
            return false;
        }
        addContact([...contacts,form]);
    }
  return (
    <div>
        <Formik
      initialValues={{
        username: '',
        email: '',
      }}
      
      onSubmit={(values) => {
        addContact([...contacts,values]);
        resetForm();
      }}
    >
       { ({handleSubmit,handleChange})=>(
        <form onSubmit={handleSubmit}>
        <div><input name='username' placeholder='User Name'  onChange={handleChange}></input></div>
        <div><input name='email' placeholder='Email'  onChange={handleChange}></input></div>
        <div><button onClick={handleSubmit} >Add</button></div></form>

        )}
    </Formik>
        
    </div>
  )
}

export default Form;
